import React from "react";
import staff from "../data/staffconfig.json";
import projects from "../data/projectsconfig.json";

const Home = () => {
  return (
    <div className="p-4">
      <h1 className="text-xl font-bold mb-4">Benvenuti su Overclocked</h1>

      {/* Sezione Staff */}
      <section className="mb-8">
        <h2 className="text-xl font-semibold mb-2">Il Nostro Team</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
          {staff.map((member) => (
            <div
              key={member.id}
              className="p-4 border rounded shadow-sm bg-white"
            >
              <img
                src={member.image}
                alt={member.name}
                className="w-full h-32 object-cover rounded mb-2"
              />
              <h3 className="text-lg font-semibold">{member.name}</h3>
              <p className="text-gray-600">{member.role}</p>
            </div>
          ))}
        </div>
      </section>

      {/* Sezione Progetti */}
      <section>
        <h2 className="text-xl font-semibold mb-2">I Nostri Progetti</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
          {projects.map((project) => (
            <div
              key={project.id}
              className="p-4 border rounded shadow-sm bg-white"
            >
              <img
                src={project.image}
                alt={project.title}
                className="w-full h-32 object-cover rounded mb-2"
              />
              <h3 className="text-lg font-semibold">{project.title}</h3>
              <p className="text-gray-600">{project.description}</p>
              <a
                href={project.url}
                target="_blank"
                rel="noopener noreferrer"
                className="text-blue-500 underline"
              >
                Scopri di pi√π
              </a>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default Home;
